import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [portfolio, setPortfolio] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("https://mcsbt-integration-416418.ew.r.appspot.com/api/portfolio")
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          setError(data.error);
        } else {
          setPortfolio(data);
        }
      })
      .catch((error) => {
        console.error(error);
        setError("An error occurred while fetching data.");
      });
  }, []);

  if (error) {
    return <div className="App">Error: {error}</div>;
  }

  if (!portfolio) {
    return <div className="App">Loading...</div>;
  }

  return (
    <div className="App">
      <h1>Portfolio for {portfolio.username}</h1>
      <h2>Total Portfolio Value: {portfolio.total_port_val.toFixed(2)}</h2>
      <div className="stocks">
        {Object.entries(portfolio.stocks).map(([symbol, stockInfo]) => (
          <div key={symbol} className="stock">
            <h3>{symbol}</h3>
            <p>Number of Stocks: {stockInfo.num_stocks}</p>
            <p>Last Closing Price: ${stockInfo.last_close.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
