import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [portfolio, setPortfolio] = useState(null);
  const [total, setTotal] = useState(0);
  const [error, setError] = useState("");
  const [list, setList] = useState(null);
  const [selectedStock, setSelectedStock] = useState(null);
  const [selectedInterval, setSelectedInterval] = useState("daily");
  const [newQuantity, setNewQuantity] = useState("");
  const userId = "user1";
  // const url = `https://mcsbt-integration-416418.ew.r.appspot.com`;
  const url = `http://127.0.0.1:5001`;

  useEffect(() => {
    async function fetchData() {
      await fetch(`${url}/api/portfolio`)
        .then((response) => response.json())
        .then((data) => {
          console.log("Fetched portfolio data:", data);
          setList(data); // Update the state with the fetched data
        })
        .catch((error) => {
          console.error("Error fetching data:", error);
          // If there's an error, i set some error state and display it
        });
    }
    fetchData();
    fetch("http://localhost:5001/api/portfolio/update_user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        user_id: 1,
        new_username: "newuser",
        new_password: "newpass",
      }),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleStockSelection = (symbol) => setSelectedStock(symbol);

  const handleUpdateStock = async () => {
    try {
      const response = await fetch(`${url}/api/portfolio/update_stock`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          symbol: selectedStock,
          quantity: newQuantity,
        }),
      });

      const data = await response.json();
      console.log(data);
      // Handle success response
    } catch (error) {
      console.error("Error updating stock:", error);
      // Handle error case
    }
  };

  console.log(`got data ${list}`);
  const [stockDetails, setStockDetails] = useState(null);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome, {userId}</h1>
      </header>
      <main>
        {error && <div className="Error-message">{error}</div>}
        {/* Portfolio and stock list display */}
        <div className="Stock-update-form">
          <input
            type="text"
            placeholder="Stock Symbol"
            value={selectedStock}
            onChange={(e) => setSelectedStock(e.target.value)}
          />
          <input
            type="number"
            placeholder="New Quantity"
            value={newQuantity}
            onChange={(e) => setNewQuantity(e.target.value)}
          />
          <button onClick={handleUpdateStock}>Update Stock</button>
        </div>
      </main>
      <footer>
        <p>&copy; {new Date().getFullYear()} Floos</p>
      </footer>
    </div>
  );
}

export default App;
