function App() {
  // ... (existing code remains unchanged)

  return (
    <div
      className="App"
      style={{ fontFamily: "Arial, sans-serif", textAlign: "center" }}
    >
      <h1>Welcome, {userId}</h1>
      {list ? (
        <>
          <h2>Total Portfolio Value: ${list.total_port_val}</h2>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              flexWrap: "wrap",
            }}
          >
            {Object.entries(list.portfolio).map(([symbol, stockInfo]) => (
              <div
                key={symbol}
                style={{
                  margin: "10px",
                  padding: "10px",
                  border: "1px solid #ddd",
                  borderRadius: "5px",
                }}
              >
                <p>
                  {symbol}: {stockInfo.num_stocks} stocks at $
                  {stockInfo.last_close}
                </p>
                <button
                  onClick={() => handleStockSelection(symbol)}
                  style={{
                    backgroundColor: "#4CAF50",
                    color: "white",
                    padding: "10px 15px",
                    margin: "5px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Select
                </button>
              </div>
            ))}
          </div>
        </>
      ) : (
        <p>Loading portfolio...</p>
      )}
      {selectedStock && (
        <div>
          <h3>Selected Stock: {selectedStock}</h3>
          {/* Rest of the code remains the same */}
        </div>
      )}
    </div>
  );
}
