import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [list, setList] = useState(null);
  const [selectedStock, setSelectedStock] = useState(null);
  const [selectedInterval, setSelectedInterval] = useState("daily");
  const userId = "user1";
  const url = `https://mcsbt-integration-416418.ew.r.appspot.com`;

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        console.log("Portfolio data:", data);
        setList(data); // Update the state with the fetched data
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const [stockDetails, setStockDetails] = useState(null);

  //...

  const fetchStockDetails = async (stockSymbol) => {
    try {
      const response = await fetch(
        `{https://mcsbt-integration-416418.ew.r.appspot.com}/portfolio/${stockSymbol}`
      );
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const details = await response.json();
      setStockDetails(details);
    } catch (error) {
      console.error("Error fetching stock details:", error);
    }
  };

  // Function to handle when a stock is selected
  const handleStockSelection = (stockSymbol) => {
    setSelectedStock(stockSymbol);
    //
  };

  // Function to handle interval change
  const handleIntervalChange = (newInterval) => {
    setSelectedInterval(newInterval);
    //
  };

  const fetchPortfolio = async () => {
    //fetches the user's portfolio dictionary.
    try {
      const response = await fetch(`${backendUrl}/${user}/portfolio`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const portfolio = await response.json();
      setPortfolio(portfolio);
    } catch (error) {
      console.error("Error fetching data: ", error);
      setError("Failed to load data.");
    }
  };

  const fetchPortfolioTotal = async () => {
    //fetches the total value of the user's portfolio.
    try {
      const response = await fetch(`${backendUrl}/${user}/portfolio/total`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const total = await response.json();
      setTotal(total);
    } catch (error) {
      console.error("Error fetching data: ", error);
      setError("Failed to load data.");
    }
  };

  return (
    <div className="App">
      <h1>Welcome, {userId}</h1>
      {list ? (
        <>
          <h2>Total Portfolio Value: ${list.total_port_val}</h2>
          <div>
            {Object.entries(list.portfolio).map(([symbol, stockInfo]) => (
              <div key={symbol}>
                <p>
                  {symbol}: {stockInfo.num_stocks} stocks at $
                  {stockInfo.last_close}
                </p>
                <button onClick={() => handleStockSelection(symbol)}>
                  Select
                </button>
              </div>
            ))}
          </div>
        </>
      ) : (
        <p>Loading portfolio...</p>
      )}
      {selectedStock && (
        <div>
          <h3>Selected Stock: {selectedStock}</h3>
          <button onClick={() => handleIntervalChange("weekly")}>Weekly</button>
          <button onClick={() => handleIntervalChange("monthly")}>
            Monthly
          </button>
          {/* Add more interval buttons */}
        </div>
      )}
    </div>
  );
}

export default App;
